import React, {Component, Fragment} from "react";
import PropTypes from 'prop-types';
import '../styles/popup.css';

class ListItems extends Component {
    render() {
        const { content } = this.props;
        if (content == null) return;
        return content.map(item => {
            <li className="popup__list-item" key={item.date}>{`${item.date}: ${item.value}`}</li>
        })

    }

}

class Popup extends Component {
    state = {
        content: []
    }

    lastData = [];

    componentDidMount() {
        const {visibility, name} = this.props;
      this.fetchPreviousData(1);
      if (visibility) {
          this.showPopup(name)
      }
    }

    showPopup(name) {
        const content = [];
        this.lastData.forEach(item => {
          const contentInfo = {
            date: item.date,
            currency: item.currency[name].Value,
          }
          content.push(contentInfo);
        })
        console.log(this.lastData, content)
      }

    fetchPreviousData(i) {
      const today = Date.now();
      let yesterday = today - (i * 86400000);
      const date = new Date(yesterday);
      const year = date.getFullYear();
      let month = date.getMonth() + 1;
      month = month > 10 ? month : `0${month}`;
      let day = date.getDate();
      day = day > 10 ? day : `0${day}`;
      let BASE_PATH = `https://www.cbr-xml-daily.ru/archive/${year}/${month}/${day}/daily_json.js`;

      fetch(BASE_PATH)
      .then((res) => res.json())
      .then((result) => {
        const info = {
          date: result.Date,
          currency: result.Valute
        }
        this.lastData.push(info)
        if (this.lastData.length < 11) {
            this.fetchPreviousData(i+1)
          } else {
            return;
          }
        })
        .catch((error => {
          if (error) {
            console.log(`No data for ${date}`);
            this.fetchPreviousData(i+1)
          }
        }))
    }

    render() {
        return <Fragment>
            <div className="popup">
                <ul className="popup__list">
                <ListItems content={this.props}/>
                </ul>
            </div>
        </Fragment>
    }

}

export default Popup;
