import React, { Component, Fragment } from 'react';
import '../styles/list.css';
import Popup from './popup';
import Tooltip from './tooltip';

function Difference(props) {
  let name = 'increase';
  if (props.currentValue < props.previousValue) {
    name = 'decrease';
  }
  return (
    <span className={name}>
      {(
        ((props.currentValue - props.previousValue) / props.previousValue) *
        100
      ).toFixed(2) + '%'}
    </span>
  );
}

class ListItems extends Component {

  state = {showPopup: false};

  lastData = [];

  componentDidMount() {
    this.fetchPreviousData(1);
  }

  fetchPreviousData(i) {
    const today = Date.now();
    let yesterday = today - (i * 86400000);
    const date = new Date(yesterday);
    const year = date.getFullYear();
    let month = date.getMonth() + 1;
    month = month > 10 ? month : `0${month}`;
    let day = date.getDate();
    day = day > 10 ? day : `0${day}`;
    let BASE_PATH = `https://www.cbr-xml-daily.ru/archive/${year}/${month}/${day}/daily_json.js`;

    fetch(BASE_PATH)
    .then((res) => res.json())
    .then((result) => {
      const info = {
        date: result.Date,
        currency: result.Valute
      }
      this.lastData.push(info)
      if (this.lastData.length < 11) {
          this.fetchPreviousData(i+1)
        } else {
          return;
        }
      })
      .catch((error => {
        if (error) {
          console.log(`No data for ${date}`);
          this.fetchPreviousData(i+1)
        }
      }))
  }

  showPopup(name) {
    const content = [];
    this.lastData.forEach(item => {
      const contentInfo = {
        date: item.date,
        currency: item.currency[name].Value,
      }
      content.push(contentInfo);
    })
    console.log(this.lastData, content)
    this.setState({showPopup: true})
  }

  render() {
    const { data } = this.props;
    const dataArr = Object.keys(data);
    return dataArr.map((elem) => (
      <li className="list__item" key={data[elem].CharCode}>
        <Tooltip content={data[elem].Name}>
          <span className="code" onClick={() => this.showPopup(data[elem].CharCode)}>{data[elem].CharCode}</span>
        </Tooltip>
        <span>{Math.round(data[elem].Value).toFixed(2)}</span>
        <Difference
          currentValue={data[elem].Value}
          previousValue={data[elem].Previous}
        />
      </li>
    ));
  }
}

class List extends Component {
  render() {
    return (
      <ul className="list">
        <ListItems data={this.props.data} />
      </ul>
    );
  }
}

class Main extends Component {
  state = {
    data: {},
  };

  componentDidMount() {
    this.fetchData();
  }

  fetchData() {
    const BASE_PATH = 'https://www.cbr-xml-daily.ru/daily_json.js';

    fetch(BASE_PATH)
      .then((res) => res.json())
      .then((result) => this.setState({ data: result.Valute }))
      .catch((error) => error);
  }

  render() {
    return (
      <main className="main">
        <List data={this.state.data} />
      </main>
    );
  }
}

export default Main;
