import React, { Component, Fragment } from 'react';
import PropTypes from 'prop-types';
import '../styles/popup.css';

class ListItems extends Component {
  render() {
    const { content } = this.props;
    if (content == null) return;
    return content.map((item) => {
      <li
        className="popup__list-item"
        key={item.date}
      >{`${item.date}: ${item.value}`}</li>;
    });
  }
}

class Popup extends Component {
  state = {
    content: [],
    visibility: false,
  };

  lastData = [];

  componentDidMount() {
    this.setState({visibility: this.props.visibility});
    console.log(this.state)
    this.fetchPreviousData(1);
  }

  fillPopup(name) {
    const previousCurrency = [];
    this.lastData.forEach((item) => {
      const contentInfo = {
        date: item.date,
        currency: item.currency[name].Value,
      };
      previousCurrency.push(contentInfo);
    });
    this.setState({ content: previousCurrency });
    // console.log(this.lastData, content)
  }



  render() {
    const { visibility, content } = this.state;
    if (visibility) {
      return (
        <Fragment>
          <div className="popup">
            <ul className="popup__list">
              <ListItems content={content} />
            </ul>
          </div>
        </Fragment>
      );
    }
    return <div></div>
  }
}

export default Popup;
